SELECT hist_users.user_id AS "USERS_ID", hist_users.name AS "USERS_NAME", hist_users.email AS "USERS_EMAIL", MAX(created_at) AS "USER_SITE_ROLE_CHANGED_AT", DATE_PART('DAY',CURRENT_DATE - MAX(CREATED_AT)) AS "DAY_COUNT_USER_SITE_ROLE_CHANGED" FROM historical_events LEFT JOIN hist_users ON historical_events.hist_target_user_id = hist_users.id INNER JOIN historical_event_types ON historical_events.historical_event_type_id = historical_event_types.type_id WHERE historical_event_types.name = 'Update User Site Role' AND HIST_USERS.USER_ID =